import{_ as o}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as e,c as i,e as a}from"./app.22353803.js";const n="/KaybeeNotes/assets/IO_blocking_model.6fed6277.png",s="/KaybeeNotes/assets/IO_nonblocking_model.cf879f1c.png",l="/KaybeeNotes/assets/IO_multiplexing_model.6dfb013e.png",r="/KaybeeNotes/assets/IO_signal_driven_model.8cd2090a.png",c="/KaybeeNotes/assets/IO_asynchronous_model.88f3df08.png",t="/KaybeeNotes/assets/IO_models_comparison.7271f8e0.png",d={},I=a('<h2 id="i-o-models" tabindex="-1"><a class="header-anchor" href="#i-o-models" aria-hidden="true">#</a> I/O Models</h2><p>\u5728 Unix \u4E0B\u6709 5 \u79CD I/O \u6A21\u578B:</p><ul><li>\u963B\u585E I/O</li><li>\u975E\u963B\u585E I/O</li><li>I/O \u591A\u8DEF\u590D\u7528 (select and poll)</li><li>\u4FE1\u53F7\u9A71\u52A8\u7684 I/O (SIGIO)</li><li>\u5F02\u6B65 I/O (the POSIX aio_ functions)</li></ul><blockquote><p>\u300AUnix \u7F51\u7EDC\u7F16\u7A0B\u300B\u4E2D\u4ECB\u7ECD\u4E86\u8FD9 5 \u79CD I/O \u6A21\u578B\uFF0C\u7531\u4E8E\u8BE5\u4E66\u4E2D\u7684\u793A\u4F8B\u56FE\u6BD4\u8F83\u6A21\u7CCA\uFF0C\u6211\u7167\u7740\u753B\u4E86\u4E00\u904D\uFF0C\u539F\u4E66\u4E2D\u4F7F\u7528\u7684\u662F UDP datagram \u4F5C\u4E3A\u8BF7\u6C42\u7684\u6570\u636E\uFF0C\u6211\u8FD9\u91CC\u7EDF\u5199\u4E3A data.</p></blockquote><h3 id="_1-\u963B\u585E-i-o-blocking-i-o" tabindex="-1"><a class="header-anchor" href="#_1-\u963B\u585E-i-o-blocking-i-o" aria-hidden="true">#</a> 1. \u963B\u585E I/O - Blocking I/O</h3><p><img src="'+n+'" alt="IO_blocking_model" loading="lazy"></p><p>\u5728\u963B\u585E\u7684 I/O \u6A21\u578B\u4E2D\uFF0C\u53D1\u8D77\u8BFB\u53D6\u8C03\u7528\u65F6\uFF0C\u5E94\u7528\u7A0B\u5E8F\u963B\u585E\u5728 <code>recvfrom</code>\u65B9\u6CD5\u4E0A\uFF0Crecvfrom \u65B9\u6CD5\u4F1A\u8FDB\u884C\u7CFB\u7EDF\u8C03\u7528\u8BFB\u53D6\u5185\u6838\u7A7A\u95F4\u4E2D\u7684\u6570\u636E\uFF0C\u5F53\u6570\u636E\u672A\u5C31\u7EEA\u65F6\uFF0C\u5219\u4F1A\u7B49\u5F85\u6570\u636E\u5C31\u7EEA\uFF1B\u5F53\u6570\u636E\u5C31\u7EEA\u65F6\uFF0C\u6570\u636E\u4F1A\u4ECE\u5185\u6838\u7A7A\u95F4\u62F7\u8D1D\u5230\u7528\u6237\u7A7A\u95F4\uFF0C\u5F53\u62F7\u8D1D\u5B8C\u6210\u540E\uFF0C<code>recvfrom</code>\u65B9\u6CD5\u8FD4\u56DE\uFF0C\u5E94\u7528\u7A0B\u5E8F\u7EE7\u7EED\u5904\u7406\u6570\u636E\u3002\u5728\u8FD9\u6574\u4E2A\u8FC7\u7A0B\u4E2D\uFF0Crecvfrom \u65B9\u6CD5\u548C\u652F\u6301\u8BE5\u65B9\u6CD5\u7684\u7CFB\u7EDF\u8C03\u7528\u4E00\u76F4\u5904\u4E8E\u963B\u585E\u72B6\u6001\uFF08\u65E0\u6CD5\u8FD4\u56DE\uFF09\uFF0C\u76F4\u5230\u8BF7\u6C42\u7684\u6570\u636E\u5230\u8FBE\u5185\u6838\uFF0C\u5E76\u4ECE\u5185\u6838\u62F7\u8D1D\u5230\u4E86\u5E94\u7528\u5185\u5B58\u4E2D\u3002</p><h3 id="_2-\u975E\u963B\u585E-i-o-nonblocking-i-o" tabindex="-1"><a class="header-anchor" href="#_2-\u975E\u963B\u585E-i-o-nonblocking-i-o" aria-hidden="true">#</a> 2. \u975E\u963B\u585E I/O - Nonblocking I/O</h3><p><img src="'+s+'" alt="IO_nonblocking_model" loading="lazy"></p><p>\u5728\u975E\u963B\u585E I/O \u4E2D\uFF0Crecvfrom \u65B9\u6CD5\u5728\u6570\u636E\u672A\u5C31\u7EEA\u65F6\u4F1A\u7ACB\u5373\u8FD4\u56DE\u4E00\u4E2A\u9519\u8BEF\uFF0C\u5E76\u4E0D\u4F1A\u963B\u585E\u5730\u7B49\u5F85\u6570\u636E\u5C31\u7EEA\u3002\u4E00\u822C\u91C7\u7528\u5FAA\u73AF\u8C03\u7528 recvfrom \u7684\u65B9\u5F0F\u6765\u68C0\u67E5\u6570\u636E\u662F\u5426\u5C31\u7EEA\uFF0C\u4E00\u65E6\u6570\u636E\u51C6\u5907\u597D\uFF0Crecvfrom \u65B9\u6CD5\u4F1A\u7B49\u5F85\u6570\u636E\u4ECE\u5185\u6838\u7A7A\u95F4\u62F7\u8D1D\u5230\u5E94\u7528\u5185\u5B58\u540E\u8FD4\u56DE\u3002</p><h3 id="_3-i-o-\u591A\u8DEF\u590D\u7528-i-o-multiplexing" tabindex="-1"><a class="header-anchor" href="#_3-i-o-\u591A\u8DEF\u590D\u7528-i-o-multiplexing" aria-hidden="true">#</a> 3. I/O \u591A\u8DEF\u590D\u7528 - I/O Multiplexing</h3><p><img src="'+l+'" alt="IO_multiplexing_model" loading="lazy"></p><p>\u5728 I/O \u591A\u8DEF\u590D\u7528\u7684\u6A21\u578B\u4E2D\uFF0C\u8FDB\u7A0B\u963B\u585E\u5728 <code>select</code>\u6216 <code>poll</code>\u65B9\u6CD5\u4E0A\uFF0C\u4E0D\u662F\u771F\u6B63\u7684 I/O \u7CFB\u7EDF\u8C03\u7528(recvfrom),\u5F53\u6570\u636E\u53EF\u8BFB\u65F6\uFF0C\u518D\u8C03\u7528 recvfrom \u65B9\u6CD5\u5C06\u6570\u636E\u62F7\u8D1D\u5230\u5E94\u7528\u5185\u5B58\u4E2D\u3002\u4E0E\u4E0A\u6587\u63D0\u5230\u7684\u963B\u585E I/O \u6A21\u578B\u5BF9\u6BD4\uFF0CI/O \u591A\u8DEF\u590D\u7528\u9700\u8981\u4F7F\u75282\u4E2A\u7CFB\u7EDF\u8C03\u7528\u6765\u5B8C\u6210\u64CD\u4F5C\uFF0C\u4F46\u5176\u4F18\u52BF\u5728\u4E8E\uFF0C\u4E00\u4E2A<code>select</code>\u65B9\u6CD5\u53EF\u4EE5\u76D1\u542C\u5728\u591A\u4E2A\u6587\u4EF6\u63CF\u8FF0\u7B26\u4E0A\u9762\uFF0C\u540C\u65F6\u76D1\u542C\u591A\u4E2A I/O \u662F\u5426\u53EF\u8BFB\u3002</p><h3 id="_4-\u4FE1\u53F7\u9A71\u52A8\u7684-i-o-signal-driven-i-o" tabindex="-1"><a class="header-anchor" href="#_4-\u4FE1\u53F7\u9A71\u52A8\u7684-i-o-signal-driven-i-o" aria-hidden="true">#</a> 4. \u4FE1\u53F7\u9A71\u52A8\u7684 I/O - Signal Driven I/O</h3><p><img src="'+r+'" alt="IO_signal_driven_model" loading="lazy"></p><p>\u5728\u4FE1\u53F7\u9A71\u52A8\u7684\u6A21\u578B\u4E2D\uFF0C\u6211\u4EEC\u5148\u6CE8\u518C\u4E00\u4E2A\u4FE1\u53F7\u5904\u7406\u5668\u5230\u7CFB\u7EDF\u8C03\u7528 <code>sigaction</code>\u4E0A\uFF0C\u8FD9\u4E2A\u8C03\u7528\u662F\u975E\u963B\u585E\u7684\uFF0C\u5E76\u4F1A\u7ACB\u5373\u8FD4\u56DE\uFF0C\u5E94\u7528\u8FDB\u7A0B\u53EF\u4EE5\u7EE7\u7EED\u5904\u7406\u540E\u9762\u7684\u903B\u8F91\uFF0C\u4E00\u65E6\u6570\u636E\u5C31\u7EEA\u65F6\u5C06\u4F1A\u4EA7\u751F\u4FE1\u53F7\uFF0C\u5E94\u7528\u8FDB\u7A0B\u53EF\u4EE5\u901A\u8FC7\u6CE8\u518C\u7684\u5904\u7406\u5668\u8BFB\u53D6\u6570\u636E\u6216\u901A\u8FC7\u8C03\u7528 recvfrom \u8BFB\u53D6\u6570\u636E\u3002\u6574\u4E2A\u8FC7\u7A0B\u5728\u7B49\u5F85\u6570\u636E\u5C31\u7EEA\u65F6\u5019\u662F\u975E\u963B\u585E\u7684\u3002</p><h3 id="_5-\u5F02\u6B65-i-o-asynchronous-i-o" tabindex="-1"><a class="header-anchor" href="#_5-\u5F02\u6B65-i-o-asynchronous-i-o" aria-hidden="true">#</a> 5. \u5F02\u6B65 I/O - Asynchronous I/O</h3><p><img src="'+c+'" alt="IO_asynchronous_model" loading="lazy"></p><p>\u5F02\u6B65 I/O \u662F\u7531 POSIX \u89C4\u8303\u5B9A\u4E49\u7684\uFF0C\u5176\u5DE5\u4F5C\u8FC7\u7A0B\u662F\u5F53\u8C03\u7528\u5F02\u6B65 I/O \u65B9\u6CD5\u65F6\u5C31\u7ACB\u5373\u8FD4\u56DE\u4E86\uFF0C\u5E94\u7528\u8FDB\u7A0B\u4F1A\u7EE7\u7EED\u53BB\u5904\u7406\u540E\u9762\u7684\u903B\u8F91\uFF0C\u5185\u6838\u4F1A\u8D1F\u8D23\u6574\u4E2A I/O \u64CD\u4F5C(\u5305\u62EC\u7B49\u5F85\u6570\u636E\u5C31\u7EEA\u548C\u5C06\u6570\u636E\u62F7\u8D1D\u81F3\u5E94\u7528\u5185\u5B58\u4E2D)\uFF0C\u4E0E\u4FE1\u53F7\u9A71\u52A8 I/O \u6A21\u578B\u7684\u533A\u522B\u5728\u4E8E\uFF0C\u5185\u6838\u5728\u4FE1\u53F7\u9A71\u52A8\u7684I/O \u4E2D\u4F1A\u901A\u77E5\u6211\u4EEC\u4F55\u65F6\u53EF\u4EE5\u5F00\u59CB I/O \u64CD\u4F5C(\u771F\u6B63\u8BFB\u6570\u636E\uFF0C\u5373\u62F7\u8D1D\u6570\u636E)\uFF0C\u800C\u5728\u5F02\u6B65 I/O \u4E2D\u5185\u6838\u662F\u901A\u77E5\u6211\u4EEC\u4F55\u65F6\u5B8C\u6210 I/O .</p><h3 id="\u5C0F\u7ED3" tabindex="-1"><a class="header-anchor" href="#\u5C0F\u7ED3" aria-hidden="true">#</a> \u5C0F\u7ED3</h3><p><img src="'+t+'" alt="IO_models_comparison" loading="lazy"></p><p>POSIX \u5B9A\u4E49\u4E86\u540C\u6B65\u548C\u5F02\u6B65 I/O \u7684\u5B9A\u4E49\uFF1A</p><ul><li>\u540C\u6B65 I/O\uFF08synchronous I/O\uFF09\uFF1AA synchronous I/O operation causes the requesting process to be blocked until that I/O operation completes.</li><li>\u5F02\u6B65 I/O\uFF08asynchronous I/O\uFF09\uFF1AAn asynchronous I/O operation does not cause the requesting process to be blocked.</li></ul><p>\u6309\u4EE5\u4E0A\u5B9A\u4EE5\u6765\u8BF4\uFF0C\u4E0A\u9762\u524D 4 \u79CD I/O \u6A21\u578B(\u963B\u585E\u3001\u975E\u963B\u585E\u3001I/O \u591A\u8DEF\u590D\u7528\u3001\u4FE1\u53F7\u9A71\u52A8 I/O)\u90FD\u5C5E\u4E8E\u540C\u6B65 I/O\uFF0C\u56E0\u4E3A\u5B83\u4EEC\u5728\u771F\u6B63\u7684 I/O \u64CD\u4F5C\u8FC7\u7A0B\u4E2D(recvfrom) \u90FD\u4F1A\u963B\u585E\u8C03\u7528\u8FDB\u7A0B\u3002\u53EA\u6709\u6700\u540E\u4E00\u4E2A\u6A21\u578B\u7B26\u5408 POSIX \u5B9A\u4E49\u7684\u5F02\u6B65 I/O.</p>',24),_=[I];function O(p,h){return e(),i("div",null,_)}const u=o(d,[["render",O],["__file","IO_Models.html.vue"]]);export{u as default};
