import{_ as n}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as l,c as r,a as e,d as t,b as o,e as a,r as c}from"./app.72a010d3.js";const d={},s=e("h3",{id:"review",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#review","aria-hidden":"true"},"#"),o(" Review")],-1),h={href:"http://blog.thefirehoseproject.com/posts/learn-to-code-and-be-self-reliant/",target:"_blank",rel:"noopener noreferrer"},p=o("The Key to Accelerating Your Coding Skills - "),u=e("strong",null,"KEN MAZAIKA",-1),_=o("\u4F5C\u8005\u7684\u4E2A\u4EBA\u4E3B\u9875\uFF1A"),g={href:"https://kenmazaika.com/about/",target:"_blank",rel:"noopener noreferrer"},f=o("https://kenmazaika.com/about/"),m=a('<p>\u4F5C\u8005\u5728\u8FD9\u7BC7\u6587\u7AE0\u4E2D\u8BF4\u660E\u4E86\u5FEB\u901F\u63D0\u5347\u7F16\u7A0B\u80FD\u529B\u7684\u5173\u952E\u70B9</p><h5 id="_2-1-\u8D77\u6B65\u9636\u6BB5" tabindex="-1"><a class="header-anchor" href="#_2-1-\u8D77\u6B65\u9636\u6BB5" aria-hidden="true">#</a> 2.1 \u8D77\u6B65\u9636\u6BB5\uFF1A</h5><ul><li>\u5B66\u4E60\u9886\u57DF\u76F8\u5173\u77E5\u8BC6(<code>domain-specific knowledge</code>)\uFF0C\u6BD4\u5982\u7528 Ruby\u600E\u4E48\u5199\u5FAA\u73AF\uFF0C\u7528\u4EE3\u7801\u4ECE\u6570\u636E\u5E93\u8BFB\u53D6\u7279\u5B9A\u6570\u636E, \u8FD9\u65B9\u9762\u7684\u77E5\u8BC6\u5F80\u5F80\u4E0E\u5177\u4F53\u7684\u7F16\u7A0B\u73AF\u5883\u76F8\u5173</li><li>\u8981\u6210\u4E3A\u4E00\u4E2A\u81EA\u5B66\u81EA\u8DB3\u5F00\u53D1\u8005\u7684\u7B2C\u4E00\u6B65\u5C31\u662F\u80FD\u591F\u5B8C\u6210\u5177\u4F53\u7684\u4EFB\u52A1\u3002</li><li>\u5BF9\u521D\u5B66\u8005\u6765\u8BF4\uFF0C\u6700\u91CD\u8981\u7684\u6280\u80FD\u662F\u80FD\u591F\u5173\u6CE8\u7EC6\u8282\uFF0C\u4E00\u662F\u80FD\u591F\u8BFB\u61C2\u9519\u8BEF\u4FE1\u606F\uFF0C\u80FD\u591F<code>Debug error message</code>, \u77E5\u9053\u53D1\u751F\u4E86\u4EC0\u4E48\uFF0C\u4E8C\u662F\u80FD\u591F\u4ECE\u6BCF\u4E00\u4E2A\u4F60\u72AF\u7684\u9519\u8BEF\u4E2D\u53BB\u5B66\u4E60\uFF0C\u5F53\u4E0B\u6B21\u518D\u9047\u5230\u7684\u65F6\u5019\u5C31\u80FD\u591F\u5FEB\u901F\u7684\u89E3\u51B3</li><li>\u5584\u7528 Google \u8FDB\u884C\u641C\u7D22</li><li><code>\u4F60\u6C38\u8FDC\u4E0D\u53EF\u80FD\u77E5\u9053\u6240\u6709\u7684\u77E5\u8BC6\u6765\u89E3\u51B3\u6240\u6709\u7684\u95EE\u9898\uFF0C\u8981\u4FDD\u6301\u7EC8\u751F\u5B66\u4E60\u7684\u6001\u5EA6</code></li></ul><p>\u5F53\u4E0B\u9762\u8FD9\u4E9B\u60C5\u5F62\u53D1\u751F\u7684\u65F6\u5019\uFF0C\u8BF4\u660E\u4F60\u5DF2\u7ECF\u5EA6\u8FC7\u8D77\u6B65\u9636\u6BB5\u8FDB\u5165\u4E0B\u4E00\u9636\u6BB5\u4E86\uFF1A</p><ul><li>\u5F53\u4F60\u9047\u5230\u5F88\u591A\u9519\u8BEF\u4FE1\u606F\u65F6\u4F60\u5DF2\u7ECF\u4E0D\u5728\u50CF\u5F00\u59CB\u65F6\u90A3\u6837\u5BB3\u6015\uFF0C\u53CD\u800C\u80FD\u591F\u77E5\u9053\u600E\u4E48\u53BB\u89E3\u51B3\u4ED6\u4EEC</li><li>\u5F53\u4F60\u5341\u5206\u64C5\u957F\u901A\u8FC7\u641C\u7D22\u5F15\u64CE\u6765\u5BFB\u627E\u89E3\u51B3\u65B9\u6848</li><li>\u5F53\u4F60\u80FD\u591F\u4ECE\u66FE\u7ECF\u5199\u8FC7\u7684\u4EE3\u7801\u4E2D\u6765\u5B66\u4E60\uFF0C\u800C\u4E0D\u662F\u4ECE\u5934\u5F00\u59CB\u4E00\u6B65\u4E00\u6B65\u7684\u91CD\u65B0\u6765\u8FC7</li></ul><h5 id="_2-2-\u8F6C\u6298\u70B9" tabindex="-1"><a class="header-anchor" href="#_2-2-\u8F6C\u6298\u70B9" aria-hidden="true">#</a> 2.2 \u8F6C\u6298\u70B9</h5>',6),b=o("![]("),k={href:"https://github.com/LiuKay/markdown_pics/blob/main/img/20200531090719.png",target:"_blank",rel:"noopener noreferrer"},w=o("markdown_pics/20200531090719.png at main \xB7 LiuKay/markdown_pics (github.com)"),y=o(")"),x=a('<p>\u8FD9\u4E2A\u9636\u6BB5\u79F0\u4E3A<code>The Inflection Point</code>, \u867D\u7136\u53EA\u662F\u6574\u4E2A\u5B66\u4E60\u8FC7\u7A0B\u7684\u4E00\u90E8\u5206\uFF0C\u4F46\u5374\u662F\u6700\u91CD\u8981\u7684\u4E00\u4E2A\u9636\u6BB5\uFF0C\u56E0\u4E3A\u5728\u8FD9\u4E2A\u9636\u6BB5\u4F60\u5C06\u8981\u9762\u5BF9\u7684\u662F\u90A3\u4E9B\u6CA1\u6709\u73B0\u6210\u7B54\u6848\u7684\u95EE\u9898\u3002</p><p>\u5F88\u591A\u65F6\u5019\u4F60\u4F1A\u89C9\u5F97\u81EA\u5DF1\u5E76\u6CA1\u6709\u51C6\u5907\u597D\uFF0C\u800C\u4E14\u503E\u5411\u4E8E\u53BB\u505A\u4E00\u4E9B\u57FA\u7840\u6027\u7684\u5DE5\u4F5C\uFF0C\u6BD4\u5982\u4E00\u4E9B<code>Get started</code>\u5DE5\u4F5C\u3002\u8FD9\u4E2A\u9636\u6BB5\u4F60\u751A\u81F3\u4F1A\u6000\u7591\u81EA\u5DF1\u662F\u4E0D\u662F\u771F\u7684\u9002\u5408\u505A\u4E00\u540D\u5F00\u53D1\u4EBA\u5458\u3002\u4E00\u4E2A\u5F88\u91CD\u8981\u7684\u539F\u56E0\u662F\uFF1A</p><blockquote><p><code>During the inflection phase, you will be coding 10-20 times SLOWER than in the previous phase </code></p></blockquote><p><code>\u5728\u8FD9\u4E2A\u8F6C\u6298\u70B9\u4E0A\uFF0C\u4F60\u7684\u7F16\u7801\u901F\u5EA6\u5C06\u4F1A\u6BD4\u5165\u95E8\u65F6\u616210\u523020\u500D</code></p><p>\u5C3D\u7BA1\u5728\u611F\u89C9\u4E0A\u4F60\u4F1A\u89C9\u5F97\u81EA\u5DF1\u8FDB\u6B65\u5F88\u6162\uFF0C\u751A\u81F3\u81EA\u6211\u6000\u7591\uFF0C\u4F46\u662F\u5B9E\u9645\u4E0A\uFF0C\u4F60\u5176\u5B9E\u5728\u90A3\u4E9B\u6700\u91CD\u8981\u7684\u4E8B\u60C5\u4E0A\u53D6\u5F97\u8FDB\u6B65\u3002</p><p>\u8FD9\u91CC\u4F5C\u8005\u5F15\u51FA\u4E86\u76F8\u5BF9\u4E8E<code>domain-specific knowledge</code> \u7684\u53E6\u4E00\u79CD\u77E5\u8BC6\uFF1A<code>procedural knowledge</code></p><blockquote><p><code>Procedural knowledge is the ability to teach yourself what you don\u2019t know along the way.</code></p></blockquote><p>\u7B80\u5355\u6765\u8BF4\u5C31\u662F\u5728\u5B66\u4E60\u8FC7\u7A0B\u4E2D\u5982\u4F55\u81EA\u5B66\u81EA\u5DF1\u4E0D\u77E5\u9053\u7684\u4E1C\u897F\u7684\u80FD\u529B\u3002\u6BD4\u5982\u4F60\u8981\u5B9E\u73B0\u4E00\u4E2A\u65B0\u7684\u529F\u80FD\uFF0C\u4F60\u8BE5\u5982\u4F55\u5F00\u59CB\uFF0C\u5982\u4F55\u8BBE\u8BA1\uFF0C\u5982\u4F55\u5B9E\u73B0\uFF1F</p><p>\u5BF9\u4E8E\u5F00\u53D1\u8005\u6765\u8BF4\uFF0C<code>domain-specific knowledge</code>\u548C<code>precedural knowledge</code>\u540C\u6837\u91CD\u8981\u3002</p><p>\u4F5C\u8005\u7ED9\u51FA\u4E86\u5BF9\u5F00\u53D1\u8005\u7684\u4E2D\u80AF\u5EFA\u8BAE\uFF1A</p><p><code>For the rest of your life, go outside your limits every single day</code></p><p>\u5BF9\u4E8E\u5F88\u591A\u5DE5\u7A0B\u5E08\u6765\u8BF4\uFF0C\u4ED6\u4EEC\u4E00\u65E6\u627E\u5230\u81EA\u5DF1\u7684\u8212\u9002\u533A\u5C31\u4F1A\u88F9\u8DB3\u4E0D\u524D\uFF0C\u4ED6\u4EEC\u5927\u591A\u6570\u4EE5\u7EF4\u62A4\u8005\u7684\u8EAB\u4EFD\u51FA\u73B0\u3002\u800C\u4F60\u5E94\u8BE5\u8FFD\u5BFB\u7684\u662F\u90A3\u4E9B\u6BCF\u5929\u90FD\u5728\u5BFB\u627E\u7A81\u7834\u81EA\u5DF1\u80FD\u529B\u9650\u5236\u7684\u4EBA\uFF0C\u5C1D\u8BD5\u53BB\u89E3\u51B3\u90A3\u4E9B\u5728\u4F60\u6280\u672F\u6808\u4E4B\u5916\u7684\u95EE\u9898\uFF0C\u4EE5\u6B64\u6765\u6269\u5C55\u4F60\u7684\u6280\u80FD\u3002</p><h5 id="_2-3-\u4E24\u4E2A\u62D0\u70B9" tabindex="-1"><a class="header-anchor" href="#_2-3-\u4E24\u4E2A\u62D0\u70B9" aria-hidden="true">#</a> 2.3 \u4E24\u4E2A\u62D0\u70B9</h5><ol><li><p>The web development inflection point</p><p>\u4F60\u6709\u80FD\u529B\u6784\u5EFA\u4EFB\u4F55\u6570\u636E\u5E93\u9A71\u52A8\u7684\u7CFB\u7EDF \uFF0C\u6211\u4EEC\u901A\u5E38\u6765\u8BF4\u53EB\u638C\u63E1\u4E86CRUD</p></li><li><p>The algorithm and data structures inflection point</p><p>\u7B97\u6CD5\u548C\u6570\u636E\u7ED3\u6784\uFF0C\u638C\u63E1\u6570\u636E\u7ED3\u6784\u548C\u7B97\u6CD5\u610F\u5473\u7740\u638C\u63E1\u4E86\u67D0\u4E00\u95E8\u7F16\u7A0B\u8BED\u8A00\u7684\u57FA\u672C\u77E5\u8BC6\uFF0C\u4EE5\u53CA\u64CD\u7EB5\u6570\u636E\u548C\u80FD\u591F\u7406\u89E3\u4E00\u4E9B\u6027\u80FD\u4E0A\u7684\u53D6\u820D</p></li></ol><h5 id="_2-4-\u5173\u4E8E\u62D0\u70B9\u7684\u7ED3\u8BBA" tabindex="-1"><a class="header-anchor" href="#_2-4-\u5173\u4E8E\u62D0\u70B9\u7684\u7ED3\u8BBA" aria-hidden="true">#</a> 2.4 \u5173\u4E8E\u62D0\u70B9\u7684\u7ED3\u8BBA</h5><blockquote><p><code>When learning to code,domain-specific knowledge doesn\u2019t matter in the grand scheme of things</code></p></blockquote><p>\u5F53\u5B66\u4E60\u7F16\u7801\u65F6\uFF0C<code>domain-specific knowleage</code>\u5176\u5B9E\u5E76\u6CA1\u90A3\u4E48\u91CD\u8981\uFF0C\u771F\u6B63\u91CD\u8981\u7684\u662F\uFF1A</p><ul><li>\u4F60\u5BF9 web \u5F00\u53D1\u6846\u67B6\u6709\u4E00\u4E2A\u624E\u5B9E\u7684\u7406\u89E3</li><li>\u80FD\u591F\u638C\u63E1\u7528\u4EFB\u4F55\u4E00\u79CD\u4F60\u6240\u719F\u6089\u7684\u8BED\u8A00\u5199\u51FA\u590D\u6742\u7B97\u6CD5\u7684\u5B9E\u73B0</li></ul><p>\u8FD9\u91CC\u4F5C\u8005\u7528\u5404\u5927\u5382\u4E3A\u4EC0\u4E48\u5982\u6B64\u770B\u91CD\u7B97\u6CD5\u548C\u6570\u636E\u7ED3\u6784\u6765\u4E3E\u4F8B\u8BF4\u660E\uFF0C\u771F\u6B63\u91CD\u8981\u7684\u4E1C\u897F\u5176\u5B9E\u5C31\u662F\u8FD92\u6837\u3002</p><h5 id="_2-5-\u5728\u62D0\u70B9\u4F60\u5C06\u5B66\u4F1A\u7684" tabindex="-1"><a class="header-anchor" href="#_2-5-\u5728\u62D0\u70B9\u4F60\u5C06\u5B66\u4F1A\u7684" aria-hidden="true">#</a> 2.5 \u5728\u62D0\u70B9\u4F60\u5C06\u5B66\u4F1A\u7684</h5><p>\u5BF9\u4E8E\u5F00\u53D1\u8005\u6765\u8BF4\uFF0C\u6700\u597D\u7684\u53C2\u8003\u5C31\u662F\u4F60\u66FE\u7ECF\u5199\u8FC7\u7684\u7C7B\u4F3C\u7684\u4EE3\u7801\uFF0C\u5F53\u4F60\u5B8C\u5168\u7406\u89E3\u4E86\u4F60\u6240\u5199\u7684\u4E1C\u897F\uFF0C\u4F60\u6839\u672C\u4E0D\u7528\u53BB\u8BB0\u4ED6\u4EEC\uFF0C\u6240\u4EE5\u6BCF\u6B21\u5F00\u53D1\u4E00\u4E2A\u65B0\u7684\u529F\u80FD\u65F6\u8BF7\u5148\u53CD\u95EE\u4E00\u4E0B\u81EA\u5DF1 :\u201C\u6211\u662F\u4E0D\u662F\u5199\u8FC7\u7C7B\u4F3C\u7684\u4EE3\u7801\uFF1F\u201D\u5982\u679C\u662F\uFF0C\u8BF7\u7FFB\u51FA\u4EE5\u524D\u7684\u4EE3\u7801\u5E76\u9010\u884C\u89E3\u91CA\u7ED9\u81EA\u5DF1\uFF0C\u73B0\u5728\u662F\u5426\u80FD\u591F\u91CD\u7528?</p><h5 id="_2-6-\u9AD8\u6548\u901A\u8FC7\u62D0\u70B9\u7684\u7B56\u7565" tabindex="-1"><a class="header-anchor" href="#_2-6-\u9AD8\u6548\u901A\u8FC7\u62D0\u70B9\u7684\u7B56\u7565" aria-hidden="true">#</a> 2.6 \u9AD8\u6548\u901A\u8FC7\u62D0\u70B9\u7684\u7B56\u7565</h5><p><strong>During the tutorial phase, take breaks from going over structured material and give yourself challenge problems along the way</strong></p><ul><li>try to do something that is outside the scope of the tutorial - \u5C3D\u91CF\u505A\u4E00\u4E9B\u8D85\u51FA\u5165\u95E8\u6559\u7A0B\u7684\u5C1D\u8BD5</li><li>Try to use tutorials as little as possible - \u5C11\u4F7F\u7528\u8F85\u5BFC\u6559\u7A0B\uFF0C\u591A\u53C2\u8003 GitHub \u6587\u6863</li><li>Focus on the essentials and use repetition - \u523B\u610F\u7EC3\u4E60\uFF0C\u91CD\u590D\u7EC3\u4E60</li></ul><p>\u901A\u8FC7\u62D0\u70B9\u662F\u4E00\u4EF6\u5F88\u6709\u6311\u6218\u6027\u7684\u4E8B\uFF0C\u7406\u89E3\u8FD9\u4E9B\u80FD\u591F\u5E2E\u52A9\u4F60\u66F4\u597D\u7684\u5B66\u4E60\uFF1A</p><ul><li>\u660E\u767D\u8FD9\u4E2A\u9636\u6BB5\u672C\u8EAB\u5C31\u662F\u56F0\u96BE\u548C\u4E0D\u90A3\u4E48\u8F7B\u677E\u7684\uFF0C</li><li>\u660E\u767D\u5728\u8FD9\u4E2A\u9636\u6BB5\u5176\u5B9E\u662F\u5728\u8DDF\u4F60\u81EA\u5DF1\u7684\u4FE1\u5FC3\u505A\u6597\u4E89\uFF0C\u4F60\u7684\u611F\u53D7\u5176\u5B9E\u5F88\u5E38\u89C1\uFF0C\u60F3\u60F3\u90A3\u4E9B\u6BD4\u4F60\u725B\u7684\u4EBA</li><li>\u6301\u7EED\u7684\u52AA\u529B\uFF0C\u4F46\u662F\u4E0D\u8981\u8FC7\u5EA6\u900F\u652F\u81EA\u5DF1</li></ul><h5 id="_2-7-\u5982\u4F55\u77E5\u9053\u81EA\u5DF1\u5DF2\u7ECF\u901A\u8FC7\u62D0\u70B9" tabindex="-1"><a class="header-anchor" href="#_2-7-\u5982\u4F55\u77E5\u9053\u81EA\u5DF1\u5DF2\u7ECF\u901A\u8FC7\u62D0\u70B9" aria-hidden="true">#</a> 2.7 \u5982\u4F55\u77E5\u9053\u81EA\u5DF1\u5DF2\u7ECF\u901A\u8FC7\u62D0\u70B9</h5><p>The final stage of the inflection point process is acceptance. Acceptance that software development is a process of continuous learning</p><p>\u7EC8\u4E8E\u660E\u767D\u7F16\u7A0B\u5176\u5B9E\u662F\u4E0D\u65AD\u6301\u7EED\u7684\u5B66\u4E60\uFF0C\u53BB\u89E3\u51B3\u66F4\u4E3A\u590D\u6742\u7684\u95EE\u9898\u3002</p><p><code>\u5B66\u4E60\u662F\u4E3A\u4E86\u627E\u5230\u65B9\u6CD5\uFF0C\u800C\u4E0D\u662F\u95EE\u9898\u7684\u7B54\u6848\u3002</code></p>',30);function v(T,q){const i=c("ExternalLinkIcon");return l(),r("div",null,[s,e("blockquote",null,[e("p",null,[e("a",h,[p,u,t(i)])]),e("p",null,[_,e("a",g,[f,t(i)])])]),m,e("p",null,[b,e("a",k,[w,t(i)]),y]),x])}const C=n(d,[["render",v],["__file","The Key to Accelerating Your Coding Skills.html.vue"]]);export{C as default};
