import{_ as n}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as c,c as i,a as e,d as r,b as t,e as o,r as s}from"./app.7879224c.js";const d={},l={href:"https://dzone.com/articles/understanding-reactor-pattern-thread-based-and-eve",target:"_blank",rel:"noopener noreferrer"},h=t("Understanding Reactor Pattern: Thread-Based and Event-Driven - DZone Java"),p=o('<p>(\u539F\u6587\u7684\u56FE\u7247\u6302\u4E86\uFF0C\u8FD9\u662F\u6211\u81EA\u5DF1\u52A0\u7684\uFF0C\u4F7F\u7528\u7684\u662F Doug Lea \u7684PPT\u4E2D\u7684)</p><p>Reactor \u6A21\u5F0F\u5728\u5B9E\u8DF5\u4E2D\u662F\u600E\u4E48\u505A\u7684\uFF0C\u4E3A\u4EC0\u4E48\u7406\u89E3\u5B83\u5F88\u91CD\u8981\u3002</p><p>\u67092\u79CD\u5904\u7406\u7F51\u7EDC\u8BF7\u6C42\u7684 Web \u67B6\u6784: \u57FA\u4E8E\u7EBF\u7A0B\uFF08thread-based\uFF09\u7684\u67B6\u6784\u548C\u4E8B\u4EF6\u9A71\u52A8\uFF08event-driven\uFF09\u7684\u67B6\u6784\u3002</p><h3 id="\u57FA\u4E8E\u7EBF\u7A0B\u7684\u67B6\u6784-thread-based-architecture" tabindex="-1"><a class="header-anchor" href="#\u57FA\u4E8E\u7EBF\u7A0B\u7684\u67B6\u6784-thread-based-architecture" aria-hidden="true">#</a> \u57FA\u4E8E\u7EBF\u7A0B\u7684\u67B6\u6784 - Thread-Based Architecture</h3><p>\u5B9E\u73B0\u591A\u7EBF\u7A0B\u670D\u52A1\u5668\u7684\u6700\u76F4\u89C2\u7684\u65B9\u6CD5\u5C31\u662F\u9075\u5FAA\u201C\u4E00\u4E2A\u7EBF\u7A0B\u5BF9\u5E94\u4E00\u4E2A\u8FDE\u63A5\u201D\uFF08thread-per-connection\uFF09\u7684\u65B9\u6CD5\u3002\u8FD9\u79CD\u65B9\u5F0F\u9002\u7528\u4E8E\u9700\u8981\u907F\u514D\u4F7F\u7528\u591A\u7EBF\u7A0B\u6765\u517C\u5BB9\u975E\u7EBF\u7A0B\u5B89\u5168\u5E93\u7684\u7F51\u7AD9\u3002</p><p>\u8FD9\u79CD\u6A21\u5F0F\u4F7F\u7528\u591A\u8FDB\u7A0B\u5904\u7406\u6A21\u5757\u6765\u9694\u79BB\u6BCF\u4E2A\u8BF7\u6C42\uFF0C\u8FD9\u6837\u5355\u4E2A\u8BF7\u6C42\u5982\u679C\u53D1\u751F\u4E86\u95EE\u9898\uFF0C\u4E0D\u4F1A\u5F71\u54CD\u5176\u4ED6\u8BF7\u6C42\uFF1A</p><p><img src="https://img-blog.csdnimg.cn/img_convert/90969c5bc197fedf4a25123bb1728120.png" alt="class service design" loading="lazy"></p><p>\u4F46\u662F\uFF0C\u591A\u8FDB\u7A0B\u4F1A\u6BD4\u8F83\u91CD\uFF0C\u5C24\u5176\u662F\u5728\u4E0A\u4E0B\u6587\u5207\u6362\u548C\u5185\u5B58\u5F00\u9500\u65B9\u9762\u3002\u6240\u4EE5\u901A\u5E38\u7684\u505A\u6CD5\u4F7F\u7528\u591A\u7EBF\u7A0B\u6765\u4EE3\u66FF\u591A\u8FDB\u7A0B\uFF0C\u5C3D\u7BA1\u591A\u7EBF\u7A0B\u7684\u5E94\u7528\u5BB9\u6613\u53D1\u751F\u95EE\u9898\u548C\u96BE\u4EE5\u8C03\u8BD5\uFF0C\u4F46\u662F\u201C\u4E00\u4E2A\u8FDE\u63A5\u4E00\u4E2A\u7EBF\u7A0B\u201D\u7684\u6A21\u5F0F\u6BD4\u201C\u4E00\u4E2A\u8FDE\u63A5\u4E00\u4E2A\u8FDB\u7A0B\u201D\u7684\u65B9\u5F0F\u5177\u6709\u66F4\u597D\u7684\u53EF\u6269\u5C55\u6027\u3002</p><p>\u4E3A\u4E86\u8C03\u6574\u5230\u6700\u4F73\u6027\u80FD\u7684\u7EBF\u7A0B\u6570\u91CF\uFF0C\u540C\u65F6\u907F\u514D\u7EBF\u7A0B\u521B\u5EFA/\u9500\u6BC1\u7684\u5F00\u9500\uFF0C\u901A\u5E38\u4F1A\u4F7F\u7528\u4E00\u4E2A\u5206\u53D1\u7EBF\u7A0B\uFF08dispatcher thread\uFF09\uFF0C\u4E00\u4E2A\u6709\u754C\u7684\u963B\u585E\u961F\u5217\u548C\u7EBF\u7A0B\u6C60\u6765\u5B9E\u73B0\u3002\u5206\u53D1\u7EBF\u7A0B\u963B\u585E\u5728\u76D1\u542C\u65B0\u7684\u8FDE\u63A5\uFF08\u5957\u63A5\u5B57Socket\uFF09\u4E0A\uFF0C\u4E00\u65E6\u6709\u65B0\u7684\u8FDE\u63A5\u5230\u8FBE\uFF0C\u5C31\u5C06\u5176\u53D1\u9001\u5230\u963B\u585E\u961F\u5217\u4E2D\uFF0C\u4E00\u65E6\u963B\u585E\u961F\u5217\u4E2D\u7684\u4EFB\u52A1\u6EE1\u4E86\uFF0C\u65B0\u7684\u8FDE\u63A5\u5C06\u4F1A\u88AB\u4E22\u5F03\uFF0C\u4F46\u662F\u597D\u5904\u662F\uFF0C\u5728\u963B\u585E\u961F\u5217\u4E2D\u7684\u4EFB\u52A1\u88AB\u5904\u7406\u7684\u5EF6\u8FDF\u65F6\u95F4\u662F\u53EF\u4EE5\u9884\u4F30\u7684\uFF08\u4EFB\u52A1\u7684\u6700\u5927\u5EF6\u65F6=\u961F\u5217\u957F\u5EA6*\u5E73\u5747\u6BCF\u4E2A\u4EFB\u52A1\u5904\u7406\u65F6\u95F4/\u7EBF\u7A0B\u6570\uFF09\u3002\u7EBF\u7A0B\u6C60\u4E2D\u53EF\u7528\u7684\u5DE5\u4F5C\u7EBF\u7A0B\u5C06\u5FAA\u73AF\u7684\u4ECE\u963B\u585E\u961F\u5217\u4E2D\u83B7\u53D6\u4EFB\u52A1\uFF08\u8981\u5904\u7406\u7684Socket\uFF09\u8FDB\u884C\u5904\u7406\u548C\u54CD\u5E94\u3002</p><p>\u4E0D\u8DB3\u4E4B\u5904\u5728\u4E8E\uFF0C\u5728\u8FD9\u6837\u7684\u67B6\u6784\u4E2D\uFF0C\u968F\u65F6\u90FD\u6709\u4E00\u4E2A\u7EBF\u7A0B\u548C\u4E00\u4E2A\u8FDE\u63A5\u76F8\u7ED1\u5B9A\u7684\u5173\u7CFB\u3002\u957F\u65F6\u95F4\u5B58\u6D3B\u7684\u8FDE\u63A5\u5982\u5F00\u542F\u4E86Keep-Alive\uFF0C \u5C06\u4E00\u76F4\u5360\u7528\u5DE5\u4F5C\u7EBF\u7A0B\uFF0C\u4F46\u662F\u5728\u8FD9\u4E2A\u65F6\u95F4\u91CC\u7EBF\u7A0B\u662F\u7A7A\u95F2\u7684\uFF0C\u6BD4\u5982\u8BBF\u95EE\u6587\u4EF6\uFF0C\u7F51\u7EDC\u8BF7\u6C42\u7B49\uFF0C\u5982\u679C\u540C\u65F6\u6709\u4E0A\u767E\u6216\u4E0A\u5343\u8FD9\u6837\u7684\u8FDE\u63A5\u7684\u8BDD\uFF0C\u90A3\u4E48\u8FD9\u4E48\u591A\u7684\u7EBF\u7A0B\u5C06\u4F1A\u5360\u7528\u5F88\u53EF\u89C2\u7684\u8D44\u6E90\u3002</p><h3 id="\u4E8B\u4EF6\u9A71\u52A8\u7684\u67B6\u6784-event-driven-architecture" tabindex="-1"><a class="header-anchor" href="#\u4E8B\u4EF6\u9A71\u52A8\u7684\u67B6\u6784-event-driven-architecture" aria-hidden="true">#</a> \u4E8B\u4EF6\u9A71\u52A8\u7684\u67B6\u6784 - Event-Driven Architecture</h3><p>\u4E8B\u4EF6\u9A71\u52A8\u7684\u65B9\u6848\u80FD\u591F\u5C06\u7EBF\u7A0B\u548C\u8FDE\u63A5\u8FDB\u884C\u89E3\u8026\uFF0C\u7EBF\u7A0B\u53EA\u670D\u52A1\u4E8E\u7279\u5B9A\u7684\u4E8B\u4EF6\u56DE\u8C03\u6216\u5904\u7406\u65B9\u6CD5\u4E2D\u3002</p><p>\u4E8B\u4EF6\u9A71\u52A8\u7684\u67B6\u6784\u7531\u4E8B\u4EF6\u7684\u751F\u4EA7\u8005\u548C\u6D88\u8D39\u8005\u7EC4\u6210\u3002\u4E8B\u4EF6\u7684\u751F\u4EA7\u8005\u662F\u4E8B\u4EF6\u7684\u6765\u6E90\uFF0C\u53EA\u5173\u5FC3\u54EA\u4E9B\u4E8B\u4EF6\u7684\u53D1\u751F\u3002\u800C\u4E8B\u4EF6\u7684\u6D88\u8D39\u8005\u53EA\u9700\u8981\u77E5\u9053\u4E8B\u4EF6\u53D1\u751F\u4E86\uFF0C\u5B83\u53EA\u9700\u8981\u5904\u7406\u4E8B\u4EF6\u6216\u8005\u4EC5\u4EC5\u662F\u54CD\u5E94\u4E8B\u4EF6\u3002</p><h4 id="reactor-\u6A21\u5F0F-the-reactor-pattern" tabindex="-1"><a class="header-anchor" href="#reactor-\u6A21\u5F0F-the-reactor-pattern" aria-hidden="true">#</a> Reactor \u6A21\u5F0F - The Reactor Pattern</h4><p>Reactor \u6A21\u5F0F\u662F\u4E8B\u4EF6\u9A71\u52A8\u67B6\u6784\u7684\u4E00\u79CD\u5B9E\u73B0\u65B9\u5F0F\u3002\u7B80\u5355\u6765\u8BF4\uFF0C\u5B83<strong>\u4F7F\u7528\u4E00\u4E2A\u5355\u72EC\u7684\u7EBF\u7A0B\u6765\u5FAA\u73AF\u5730\u3001\u963B\u585E\u5730\u76D1\u542C\u4E8B\u4EF6\u7684\u5C31\u7EEA\uFF0C\u5E76\u5C06\u5C31\u7EEA\u7684\u4E8B\u4EF6\u5206\u53D1\u7ED9\u76F8\u5E94\u7684\u56DE\u8C03\u51FD\u6570\u6216\u5904\u7406\u5668</strong>\u3002\u8FD9\u6837\u7684\u8BDD\u5C31\u4E0D\u9700\u8981\u963B\u585E\u5728I/O\u4E8B\u4EF6\u5904\u7406\u4E0A\u4E86\uFF0C\u53EA\u9700\u8981\u6CE8\u518C\u76F8\u5E94\u7684\u56DE\u8C03\u51FD\u6570\u6216\u5904\u7406\u65B9\u6CD5\uFF0C\u4E00\u65E6I/O\u5C31\u7EEA\u5C31\u4F1A\u81EA\u52A8\u5730\u56DE\u8C03\u5904\u7406\u3002\u4E8B\u4EF6\u53EF\u4EE5\u662F\u65B0\u7684\u8FDE\u63A5\u4E8B\u4EF6\uFF0C\u53EF\u8BFB/\u53EF\u5199\u4E8B\u4EF6\u7B49\u7B49\u3002\u7136\u540E\u8FD9\u4E9B\u56DE\u8C03\u51FD\u6570\u5728\u591A\u6838CPU\u7684\u73AF\u5883\u4E0B\u8FD8\u53EF\u4EE5\u4F7F\u7528\u7EBF\u7A0B\u6C60\u6765\u5B9E\u73B0\u3002</p><p><strong>Reactor \u6A21\u5F0F\u5B9E\u73B0\u4E86\u6A21\u5757\u5316\u5E94\u7528\u4EE3\u7801\u4E0E\u53EF\u91CD\u7528\u7684\u4E8B\u4EF6\u9A71\u52A8\u67B6\u6784\u7684\u89E3\u8026\u3002</strong></p><p>Reactor \u6A21\u5F0F\u67092\u4E2A\u91CD\u8981\u7684\u7EC4\u6210\uFF1A</p><h4 id="_1-reactor" tabindex="-1"><a class="header-anchor" href="#_1-reactor" aria-hidden="true">#</a> 1. Reactor</h4><p>\u4E00\u4E2AReactor \u5B9E\u4F8B\u8FD0\u884C\u5728\u4E00\u4E2A\u5355\u72EC\u7684\u7EBF\u7A0B\u4E2D\uFF0C\u5B83\u7684\u4F5C\u7528\u5C31\u662F\u76D1\u542C\u5230 I/O \u4E8B\u4EF6\u65F6\u5C06\u5176\u5206\u53D1\u7ED9\u76F8\u5E94\u7684\u5904\u7406\u5668\uFF08\u56DE\u8C03\u51FD\u6570\uFF09\u3002\u7C7B\u4F3C\u4E8E\u516C\u53F8\u91CC\u4E00\u4E2A\u7535\u8BDD\u63A5\u7EBF\u5458\uFF0C\u5F53\u6709\u7535\u8BDD\u63A5\u5165\u65F6\u5E2E\u5FD9\u8F6C\u63A5\u5230\u5BF9\u5E94\u7684\u8054\u7CFB\u4EBA\u3002</p><h4 id="_2-handlers" tabindex="-1"><a class="header-anchor" href="#_2-handlers" aria-hidden="true">#</a> 2. Handlers</h4><p>\u4E00\u4E2A\u5904\u7406\u5668\uFF08\u56DE\u8C03\u65B9\u6CD5\uFF09\u662F\u771F\u6B63\u5904\u7406 I/O \u4E8B\u4EF6\u7684\u5B9E\u4F53\uFF0C\u7C7B\u4F3C\u4E8E\u63A5\u7EBF\u5458\u8981\u8F6C\u63A5\u7ED9\u7684\u8054\u7CFB\u4EBA\uFF0C\u4ED6\u624D\u662F\u771F\u6B63\u8981\u63A5\u7535\u8BDD\u5904\u7406\u4E8B\u60C5\u7684\u4EBA\u3002</p><h3 id="reactor-\u6A21\u5F0F\u89E3\u51B3\u7684\u95EE\u9898" tabindex="-1"><a class="header-anchor" href="#reactor-\u6A21\u5F0F\u89E3\u51B3\u7684\u95EE\u9898" aria-hidden="true">#</a> Reactor \u6A21\u5F0F\u89E3\u51B3\u7684\u95EE\u9898</h3><p>Reactor \u67B6\u6784\u6A21\u5F0F\u4F7F\u4E8B\u4EF6\u9A71\u52A8\u7684\u5E94\u7528\u5B9E\u73B0\u4E86\u591A\u8DEF\u590D\u7528\uFF0C\u5C06\u6765\u81EA\u4E0D\u540C\u7684\u670D\u52A1\u8BF7\u6C42\u5206\u53D1\u5230\u540C\u4E00\u4E2A\u670D\u52A1\u5E94\u7528\u4E0A\u3002\u4E00\u4E2A reactor \u5B9E\u4F8B\u6301\u7EED\u76D1\u542C\u4E8B\u4EF6\uFF0C\u4E00\u65E6\u4E8B\u4EF6\u5C31\u7EEA\uFF0C\u5C31\u901A\u77E5\u76F8\u5E94\u7684\u4E8B\u4EF6\u5904\u7406\u5668\uFF08\u56DE\u8C03\u51FD\u6570\uFF09\u6765\u5904\u7406\u4E8B\u4EF6\u3002</p><p><strong>The Reactor Pattern is a design pattern for synchronous demultiplexing and order of events as they arrive\u3002</strong></p>',24),u=t("Reactor \u6A21\u5F0F\u7684\u8BBE\u8BA1\u76EE\u7684\u662F\u5B9E\u73B0\u540C\u6B65\u7684\u591A\u8DEF\u590D\u7528\uFF0C\u540C\u6B65\u662F\u6307\u6309\u7167\u4E8B\u4EF6\u5230\u8FBE\u7684\u987A\u5E8F\u5206\u53D1\u5904\u7406\u3002\u5E94\u7528\u7A0B\u5E8F\u540C\u65F6\u63A5\u6536\u4E0D\u540C\u5BA2\u6237\u7AEF\u7684\u6D88\u606F\u3001\u8BF7\u6C42\u548C\u8FDE\u63A5\uFF0C\u5C3D\u7BA1\u8FD9\u4E9B\u8BF7\u6C42\u662F\u5E76\u53D1\u7684\uFF0C\u4F46\u662F\u5E94\u7528\u7A0B\u5E8F\u4F1A\u6309\u4E8B\u4EF6\u7684\u5230\u8FBE\u987A\u5E8F\u89E6\u53D1\u56DE\u8C03\u65B9\u6CD5\u3002Reactor \u6A21\u5F0F\u662F\u4E3A\u4E86\u907F\u514D\u4E3A\u6BCF\u4E2A\u4E8B\u4EF6\uFF08\u6D88\u606F\u3001\u8BF7\u6C42\u6216\u8FDE\u63A5\u7B49\uFF09\u521B\u5EFA\u4E00\u4E2A\u65B0\u7684\u5904\u7406\u7EBF\u7A0B\uFF0C\u5B83\u5B9E\u9645\u4E0A\u63A5\u6536\u4E00\u7CFB\u5217\u7684\u4E8B\u4EF6\u6D88\u606F\u7136\u540E\u5C06\u5B83\u4EEC\u987A\u5E8F\u5730\u5206\u53D1\u5230\u5BF9\u5E94\u7684\u4E8B\u4EF6\u5904\u7406\u5668\u4E2D\u53BB\u4E86\u3002Reactor \u89E3\u51B3\u7684\u8FD9\u4E2A\u95EE\u9898\u7684\u5B9E\u8D28\u5176\u5B9E\u662F\u8457\u540D\u7684"),g=e("code",null,"C10K",-1),m=t("\u95EE\u9898\uFF08"),_={href:"http://www.kegel.com/c10k.html",target:"_blank",rel:"noopener noreferrer"},b=t("The C10K problem"),f=t(")\uFF09"),v=o('<blockquote><p><strong>In Summary: Servers have to handle more than 10,000 concurrent clients, and threads cannot scale the connections using Tomcat, Glassfish, JBoss, or HttpClient.</strong></p><p>So, the application using the reactor only needs to use a thread to handle simultaneous events.</p></blockquote><p><strong>\u603B\u7ED3\uFF1A\u5728\u9700\u8981\u5904\u7406\u8D85\u8FC710000\u4E2A\u5E76\u53D1\u8BF7\u6C42\u7684\u670D\u52A1\u5668\u573A\u666F\u4E0B\uFF0C\u4F7F\u7528\u50CF Tomcat\uFF0CGlassfish\uFF0CJBoss \u6216\u8005\u662F HttpClient \u662F\u65E0\u6CD5\u505A\u5230\u7EBF\u7A0B\u7684\u53EF\u4F38\u7F29\u7684.</strong> \u800C\u4F7F\u7528 Reactor \u6A21\u5F0F\u7684\u5E94\u7528\u53EA\u9700\u8981\u4F7F\u7528\u4E00\u4E2A\u7EBF\u7A0B\u6765\u540C\u6B65\u5904\u7406\u5E76\u53D1\u8BF7\u6C42\u3002</p><p><img src="https://img-blog.csdnimg.cn/img_convert/8cfce83b1f54120381eb6f35d476a732.png" alt="Basic Reactor Design" loading="lazy"></p><p>\u57FA\u672C\u4E0A\uFF0C\u6807\u51C6Reactor\u5141\u8BB8\u5177\u6709\u540C\u6B65\u4E8B\u4EF6\u7684\u4E3B\u5E94\u7528\u7A0B\u5E8F\uFF0C\u540C\u65F6\u4FDD\u6301\u5355\u7EBF\u7A0B\u7684\u7B80\u5355\u6027\u3002</p><p>\uFF08\u591A\u7EBF\u7A0B\u7248\u672C\uFF09</p><p><img src="https://img-blog.csdnimg.cn/img_convert/fa0ba44d876e9c79d1efa1a1d7092900.png" alt="worker thread pools" loading="lazy"></p><blockquote><p>A demultiplexer is a circuit that has an input and more than one output. It is a circuit used when you want to send a signal to one of several devices.</p><p>This description sounds similar to the description given to a decoder, but is used to select between many devices, while a demultiplexer is used to send a signal among many devices.</p></blockquote><p>(\u4E0A\u9762\u9762\u8FD9\u6BB5\u770B\u7684\u4E0D\u662F\u5F88\u61C2\uFF0C\u597D\u50CF\u662F\u62FF\u7535\u8DEF\u505A\u7C7B\u6BD4\uFF0C\u76F4\u63A5\u62FF\u8C37\u6B4C\u7FFB\u8BD1\u4E86)</p><p>\u591A\u8DEF\u9009\u62E9\u5668\u662F\u5177\u6709\u4E00\u4E2A\u8F93\u5165\u548C\u4E00\u4E2A\u4EE5\u4E0A\u8F93\u51FA\u7684\u7535\u8DEF\u3002 \u5F53\u60A8\u8981\u5C06\u4FE1\u53F7\u53D1\u9001\u5230\u591A\u4E2A\u8BBE\u5907\u4E4B\u4E00\u65F6\uFF0C\u5B83\u662F\u4E00\u79CD\u7535\u8DEF\u3002</p><p>\u8BE5\u63CF\u8FF0\u542C\u8D77\u6765\u4E0E\u5BF9\u89E3\u7801\u5668\u7684\u63CF\u8FF0\u7C7B\u4F3C\uFF0C\u4F46\u662F\u7528\u4E8E\u5728\u8BB8\u591A\u8BBE\u5907\u4E4B\u95F4\u8FDB\u884C\u9009\u62E9\uFF0C\u800C\u591A\u8DEF\u5206\u89E3\u5668\u7528\u4E8E\u5728\u8BB8\u591A\u8BBE\u5907\u4E4B\u95F4\u53D1\u9001\u4FE1\u53F7\u3002</p><p>Reactor \u5141\u8BB8\u4F7F\u7528\u5355\u4E2A\u7EBF\u7A0B\u9AD8\u6548\u5730\u5904\u7406\u963B\u585E\u7684\u591A\u4E2A\u4EFB\u52A1\uFF0C\u540C\u65F6 Reactor\u8FD8\u8981\u5BF9\u4E8B\u4EF6\u5904\u7406\u5668\uFF08\u56DE\u8C03\u65B9\u6CD5\uFF09\u8FDB\u884C\u7EC4\u7EC7\u548C\u7BA1\u7406\uFF0C\u5F53\u4E8B\u4EF6\u88AB\u6FC0\u6D3B\u65F6\uFF0CReactor \u5C06\u8BF7\u6C42\u8F6C\u53D1\u7ED9\u53EF\u7528\u7684\u5904\u7406\u5668\u8FDE\u63A5\u5E76\u5C06\u5176\u6807\u8BB0\u6210\u6FC0\u6D3B\u72B6\u6001\uFF08\u5904\u7406\u4E2D\uFF09\u3002</p><h4 id="\u4E8B\u4EF6\u5FAA\u73AF" tabindex="-1"><a class="header-anchor" href="#\u4E8B\u4EF6\u5FAA\u73AF" aria-hidden="true">#</a> \u4E8B\u4EF6\u5FAA\u73AF\uFF1A</h4><blockquote><ol><li>Find all handlers that are active and unlocked or delegates this for a dispatcher implementation.</li><li>Execute each of these handlers sequentially until complete, or a point is reached where they are blocked. Completed Handlers are deactivated, allowing the event cycle to continue.</li><li>Repeats from Step One (1)</li></ol></blockquote><ol><li>\u67E5\u627E\u6240\u6709\u5904\u4E8E\u6D3B\u52A8\u72B6\u6001\u4E14\u672A\u9501\u5B9A\u7684\u5904\u7406\u5668\uFF0C\u6216\u5C06\u5176\u59D4\u6258\u7ED9\u8C03\u5EA6\u7A0B\u5E8F\u7684\u67D0\u4E2A\u5B9E\u73B0</li><li>\u4F9D\u6B21\u6267\u884C\u6240\u6709\u7684\u5904\u7406\u5668\uFF08\u56DE\u8C03\u65B9\u6CD5\uFF09\u76F4\u5230\u6267\u884C\u5B8C\u6210\uFF0C\u6216\u963B\u585E\u3002 \u5DF2\u5B8C\u6210\u7684\u5904\u7406\u5668\u5C06\u88AB\u505C\u7528\uFF0C\u4ECE\u800C\u5141\u8BB8\u4E8B\u4EF6\u5FAA\u73AF\u7EE7\u7EED\u8FDB\u884C\u3002</li><li>\u91CD\u590D\u6B65\u9AA41</li></ol><p>\uFF08\u6CE8\uFF1A\u539F\u6587\u770B\u8D77\u6765\u6709\u70B9\u96BE\u4EE5\u7406\u89E3\uFF0C\u7FFB\u8BD1\u8FC7\u6765\u4E5F\u662F\u8FD9\u6837\uFF0C\u4F46\u662F\u5982\u679C\u4F60\u77E5\u9053Java NIO \u7684\u5B9E\u73B0 Reactor \u673A\u5236\u7684\u8BDD\u5E94\u8BE5\u5F88\u597D\u7C7B\u6BD4:</p><ol><li>\u5F00\u59CB\u5FAA\u73AF\u76D1\u542C Socket \u5C31\u7EEA\u4E8B\u4EF6\uFF08\u5982Accept,Read,Write\uFF09</li><li>\u904D\u5386\u6240\u6709\u5DF2\u5C31\u7EEA\u7684\u4E8B\u4EF6\uFF08\u904D\u5386SelectionKey\uFF09\u5E76\u5C06\u5176\u5206\u53D1\u7ED9\u5BF9\u5E94\u7684\u4E8B\u4EF6\u5904\u7406\u5668\u5904\u7406\uFF0C\u76F4\u5230\u5904\u7406\u5668\u6267\u884C\u5B8C\u6BD5\uFF0C\u7136\u540E\u5C06\u4E8B\u4EF6\u5C31\u7EEA\u72B6\u6001\u5220\u9664\uFF08\u5220\u9664\u5DF2\u5904\u7406\u7684SelectionKey\uFF09</li><li>\u8FD4\u56DE\u6B65\u9AA41\uFF09</li></ol><h3 id="\u4E3A\u4EC0\u4E48-reactor-\u6A21\u5F0F\u5F88\u91CD\u8981" tabindex="-1"><a class="header-anchor" href="#\u4E3A\u4EC0\u4E48-reactor-\u6A21\u5F0F\u5F88\u91CD\u8981" aria-hidden="true">#</a> \u4E3A\u4EC0\u4E48 Reactor \u6A21\u5F0F\u5F88\u91CD\u8981\uFF1F</h3><p>\u56E0\u4E3A Reactor \u6A21\u5F0F\u88AB\u5E94\u7528\u5728\u5F88\u591A\u5730\u65B9\uFF0C\u5982 Node.js\uFF0CVert.x\uFF0CReactive Extensions\uFF0CJetty\uFF0CNginx \u7B49\u7B49\u8FD8\u6709\u5176\u4ED6\u8BB8\u591A\u3002\u56E0\u6B64\uFF0C\u5982\u679C\u4F60\u559C\u6B22\u8BC6\u522B\u6A21\u5F0F\u5E76\u60F3\u77E5\u9053\u9690\u85CF\u5728\u4E8B\u7269\u80CC\u540E\u7684\u5DE5\u4F5C\u65B9\u5F0F\uFF0C\u90A3\u4E48\u8BF7\u52A1\u5FC5\u6CE8\u610F\u8FD9\u79CD\u6A21\u5F0F\u3002</p>',18);function R(k,x){const a=s("ExternalLinkIcon");return c(),i("div",null,[e("blockquote",null,[e("p",null,[e("a",l,[h,r(a)])])]),p,e("p",null,[u,g,m,e("a",_,[b,r(a)]),f]),v])}const S=n(d,[["render",R],["__file","reactor-model.html.vue"]]);export{S as default};
